import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import "./App.css";

const App: React.FC = () => {
  const [uploadResult, setUploadResult] = useState<any>(null);
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleQuery = async () => {
    setLoading(true);
    setError("");
    setAnswer("");
    try {
      const formData = new FormData();
      formData.append("question", query);
      const res = await fetch("http://localhost:8000/query", {
        method: "POST",
        body: formData,
      });
      if (!res.ok) throw new Error("Network response was not ok");
      const data = await res.json();
      setAnswer(data.answer);
    } catch (err: any) {
      setError(err.message || "Unknown error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-blue-600 text-white py-4 shadow">
        <h1 className="text-2xl font-bold text-center">
          Gotex â€“ The AI Gold Trading Assistant
        </h1>
      </header>
      <main className="flex flex-col items-center justify-center mt-8 w-full">
        <FileUpload onUpload={setUploadResult} />
        {uploadResult && (
          <div className="mt-6 w-full max-w-xl bg-white rounded shadow p-4">
            <h2 className="text-lg font-semibold mb-2">Upload Result</h2>
            <pre className="bg-gray-100 p-2 rounded">
              {JSON.stringify(uploadResult, null, 2)}
            </pre>
          </div>
        )}
        <div className="mt-8 w-full max-w-xl bg-white rounded shadow p-4">
          <h2 className="text-lg font-semibold mb-2">Ask Gotex</h2>
          <div className="flex">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Ask a question about gold trading..."
              className="flex-1 border rounded px-2 py-1 mr-2"
            />
            <button
              onClick={handleQuery}
              className="bg-blue-600 text-white px-4 py-2 rounded"
              disabled={loading}
            >
              {loading ? "Asking..." : "Ask"}
            </button>
          </div>
          {error && (
            <div className="mt-4 bg-red-100 text-red-700 p-2 rounded">
              <strong>Error:</strong> {error}
            </div>
          )}
          {answer && (
            <div className="mt-4 bg-gray-100 p-2 rounded">
              <strong>Answer:</strong> {answer}
            </div>
          )}
        </div>
        {/* Future: Add prediction chart here */}
      </main>
    </div>
  );
};

export default App;
