import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FileUpload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [status, setStatus] = useState<string>('');
  const [files, setFiles] = useState<string[]>([]);

  const fetchFiles = async () => {
    try {
      const response = await axios.get('http://localhost:8000/files/');
      setFiles(response.data.files);
    } catch (error) {
      setFiles([]);
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setFile(e.target.files[0]);
    }
  };

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    try {
      setStatus('Uploading...');
      const response = await axios.post('http://localhost:8000/upload/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setStatus(`Success: ${response.data.filename}`);
      setFile(null);
      fetchFiles();
    } catch (error) {
      setStatus('Upload failed');
    }
  };

  return (
    <div className="p-4 border rounded shadow-md bg-white max-w-md mx-auto mt-8">
      <h2 className="text-lg font-bold mb-2">Upload Educational Content</h2>
      <input type="file" onChange={handleFileChange} className="mb-2" />
      <button
        onClick={handleUpload}
        className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
        disabled={!file}
      >
        Upload
      </button>
      {status && <div className="mt-2 text-sm">{status}</div>}
      <div className="mt-4">
        <h3 className="font-semibold mb-1">Uploaded Files:</h3>
        <ul className="list-disc pl-5">
          {files.map((fname) => (
            <li key={fname}>{fname}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FileUpload;
